import './App.css';

import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addPhone } from './store/phones/phones'

import apiRequest from './global/apiRequest';
import { global } from './global/Global'

import Home from './components/Home';
import PhoneListContainer from './components/PhoneListContainer';


function App() {
  const phones = useSelector(state => state.phones);
  const dispatch = useDispatch();

  const [url, setUrl] = useState(global.url);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (phones.length == 0) {
      apiRequest().then(data => {
        data.forEach(element => dispatch(addPhone(element)));
        setLoading(false);
      }).catch((error => {
        alert(error);
      })); 
    }
  });



  return (
    <div className="App">


      <h2>loading: {loading}</h2>

      <PhoneListContainer phones={phones}></PhoneListContainer>



      { phones.map(phone => (

        <div key={phone.id}>

          <p>phone: {phone.phone}</p>
          <p>details: {phone.details}</p>
          <p>price: ${phone.price}</p>
          <p>year: {phone.year}</p>

          <img src={url + phone.picture} alt={phone.phone}></img>

          <hr />
        </div>

      ))}



      <Home></Home>
    </div>
  );
}

export default App;
