import './App.css';

import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addPhone } from './store/phones/phones'

import apiRequest from './global/apiRequest';
import { global } from './global/Global'
import Home from './components/Home';


function App() {
  const phones = useSelector(state => state.phones);
  const dispatch = useDispatch();

  const url = global.url;

  useEffect(() => {
    if (phones.length == 0) {
      apiRequest().then(data => {
        data.forEach(element => dispatch(addPhone(element)));
      });

      //actualizar el state para quitar el spiner 
    }
  });




  return (
    <div className="App">

      { phones.map(phone => (

        <div key={phone.id}>

          <p>id: {phone.id}</p>
          <p>phone: {phone.phone}</p>
          <p>details: {phone.details}</p>
          <p>price: {phone.price}</p>
          <p>year: {phone.year}</p>
          <p>picture: {phone.picture}</p>

          { 'http://127.0.0.1:8000' +  phone.picture  }

          <img src={ 'http://127.0.0.1:8000' +  phone.picture  }></img>



          <hr />
        </div>

      ))}






      <Home></Home>
    </div>
  );
}

export default App;
