import logo from './logo.svg';
import './App.css';

import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addPhone } from './store/phones/phones'

import apiRequest from './global/apiRequest';
import Home from './components/Home';


function App() {
  const phones = useSelector(state => state.phones);
  const dispatch = useDispatch();

  useEffect(() => {
    if (phones.length == 0){
      apiRequest().then( data => {
        data.forEach( element => dispatch(addPhone(element)));
      });

     //actualizar el state para quitar el spiner 
    } 
  });




  const newPhone = {
    phone: 'Nokia',
    year: 437,
  }


  console.log(phones.length);
  console.log(phones);


  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>

      <button onClick={() => dispatch(addPhone(newPhone))}>boton</button>

      <Home></Home>
    </div>
  );
}

export default App;
