import React, { Component } from 'react';

import { global } from '../global/Global'
import PhoneDetailComponent from './PhoneDetailComponent';


class PhoneListContainer extends Component {

    url = global.url;

    render() {
        return (
            <React.Fragment>
                <h3 className="my-4">Phone List:</h3>

                <div className="container d-flex phone-container">


                    {this.props.phones.map(phone => (
                        <div key={phone.id}>
                            <div className="card m-3 card-phone">
                                <img src={this.url + phone.picture}
                                    className="card-img-top card-image m-2" alt={phone.phone}
                                    data-bs-toggle="modal" data-bs-target={"#Modal" + phone.id}
                                />
                                <div className="card-body">
                                    <h5 className="card-title">{phone.phone}</h5>

                                    <p>price: ${phone.price}</p>
                                    <p>year: {phone.year}</p>

                                </div>
                                <ul className="list-group list-group-flush">
                                    <li className="list-group-item">An item</li>
                                    <li className="list-group-item">A second item</li>
                                    <li className="list-group-item">A third item</li>
                                </ul>

                            </div>





                            {/*  Modal  */}
                            <div className="modal fade" id={"Modal" + phone.id} tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                                <div className="modal-dialog">
                                    <div className="modal-content">
                                        <div className="modal-header">
                                            <h5 className="modal-title" id="exampleModalLabel">{phone.phone}</h5>
                                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                                        </div>
                                        <div className="modal-body">
                                            <p>{phone.details}</p>

                                            <img src={this.url + phone.picture}
                                                className="card-img-top card-image m-2" alt={phone.phone}
                                            />
                                        </div>
                                        <div className="modal-footer">
                                            <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                                        </div>
                                    </div>
                                </div>
                            </div>



                            <PhoneDetailComponent />



                        </div>
                    ))}












                </div>






            </React.Fragment>




        )
    }
}

export default PhoneListContainer;
