import './App.css';

import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addPhone } from './store/phones/phones'

import apiRequest from './global/apiRequest';
import Home from './components/Home';


function App() {
  const phones = useSelector(state => state.phones);
  const dispatch = useDispatch();

  useEffect(() => {
    if (phones.length == 0){
      apiRequest().then( data => {
        data.forEach( element => dispatch(addPhone(element)));
      });

     //actualizar el state para quitar el spiner 
    } 
  });




  const newPhone = {
    phone: 'Nokia',
    year: 437,
  }


  console.log(phones.length);
  console.log(phones);


  return (
    <div className="App">


      <button onClick={() => dispatch(addPhone(newPhone))}>boton</button>

      <Home></Home>
    </div>
  );
}

export default App;
