import { combineReducers } from "redux";

const ADD_PHONE = 'ADD_PHONE';


export function addPhone(phone) {
    return {
        type: ADD_PHONE,
        phone,
    }
}


const defaultPhones = [
    {
        phone: 'Nokia',
        year: 437,
    }
]


function phones(state = defaultPhones, action) {
    if (action.type == ADD_PHONE) {
        console.log('agregando');
        return [...state, action.phone];
    }
    console.log('devoliendo nada')
    return state;
}



const phoneApp = combineReducers({ phones });

export default phoneApp;
